<?php  class ChubopsController {    public function index() {		/*get the series #*/		isset($_GET['series']) ? $series = $_GET['series'] : $series = 1;		/*fetch the chubops based on series*/		$chubops = Chubops::fetchChubopImages($series);		$pages = Chubops::fetchPages('chubops');		for ($i=0; $i < count($pages); $i++) {			switch ($pages[$i]['page']) {				case 'chubops_top':				$top = $pages[$i]['content'];				break;				case 'chubops_body':				$body = $pages[$i]['content'];				break;				case 'chubops_bottom':				$bottom = $pages[$i]['content'];				break;			}			}				require_once('views/pages/chubops.php');	}	public function error() {	  require_once('views/pages/error.php');	}  }?>