<?php  class HitpopsController {    public function index() {		/*get the series #*/		isset($_GET['series']) ? $series = $_GET['series'] : $series = 'E';		/*fetch the hitpops based on series*/		$hitpops = Chubops::fetchHitpopImages($series);				$pages = Chubops::fetchPages('hitpops');		for ($i=0; $i < count($pages); $i++) {			switch ($pages[$i]['page']) {				case 'hitpops_top':				$top = $pages[$i]['content'];				break;				case 'hitpops_body':				$body = $pages[$i]['content'];				break;				case 'hitpops_bottom':				$bottom = $pages[$i]['content'];				break;			}			}				require_once('views/pages/hitpops.php');	}	public function error() {	  require_once('views/pages/error.php');	}  }?>